mapper.select:
SELECT alarm_context
FROM alarm
WHERE alarm_id = #{alarmId}

SELECT COUNT(*) > 0
FROM alarm
WHERE alarm_id = #{alarmId}

mapper.update:
UPDATE members_alarm
SET alarm_is_active = 0
WHERE members_alarm_id = #{memberAlarmId}
  AND member_id = #{memberId}

mapper.select:
SELECT member_id
FROM members_alarm
WHERE members_alarm_id = #{memberAlarmId}
